const Spinnies = require("./");

const spinnies = new Spinnies();

(async () => {
  spinnies.add("spinner-1", "test spinner");

  await delay(1000);

  spinnies.add("spinner-2", {
    text: "another test spinner",
    textColor: "gray",
    prefixColor: "greenBright"
  });

  await delay(1000);

  spinnies.add("spinner-3", {
    text: "I'm not feeling so good",
    prefixColor: "redBright",
    textColor: "yellow"
  });

  await delay(1000);

  spinnies.add("spinner-4", {
    text: "I feel a warning coming on!"
  });

  await delay(1000);

  spinnies.add("spinner-5", {
    text: "Go on, remove me... I dare you."
  });
  
  spinnies.add("spinner-6", {
    text: "Go on, remove me... I dare you."
  });

  await delay(1000);

  spinnies.succeed("spinner-1", {
    text: "success!",
    textColor: "none",
    prefixColor: "green"
  });

  await delay(1000);

  spinnies.stop("spinner-2", {
    text: "Whelp, at least that's over",
    textColor: "none"
  });

  await delay(1000);

  spinnies.fail("spinner-3", {
    text: "woof!",
    textColor: "none",
    prefixColor: "red"
  });

  await delay(1000);

  spinnies.warn("spinner-4", "I tried warning you!");

  await delay(1000);

  spinnies.remove("spinner-5");

  await delay(1000);

  spinnies.remove("spinner-6");

})();

function delay(time) {
  return new Promise(resolve => {
    setTimeout(resolve, time);
  });
}


